const express = require("express");
const router = express.Router();
const {createItinerary, itineraryUpdate, itineraryGet } = require("../controller/itineraryController");
const {
  registeruser,
  loginUser,
  Logout,
} = require("../controller/userController");
const { authenticate, refreshToken } = require("../auth/auth");

router.post("/Register", registeruser);
router.post("/login", loginUser);

router.post("itinerary", authenticate, createitinarary);
router.put("itineraryUpdate", authenticate, itinararyupdate);
router.get("itineraryGet", authenticate, itinararyget);

router.all("/*", (req, res) => {
  res.status(400).send({ status: false, message: "Invalid path params" });
});

module.exports = router;
